function newIma = histT(Ima)%Function to Perform a Histogram Transformation of a Image c = 'y'; while(c == 'y')   printf('\n')   C = input('What is the value of c? (-c for inversion) ');   a = input('What is the value of a? (0 if use L) ');   L = input('Whats is the value of L? (0 if use a) ');   newIma = double(Ima);   if(C < 1)      newIma = abs((newIma .- 255));      C = abs(C);    endif   if(L == 0)    newIma = C.*(newIma .+ a);   elseif(L > 0)    newIma = (C.*(newIma .- 256*L) .+ 256*L);   endif      newIma(newIma(:,:) > 255) = 255;   newIma(newIma(:,:) < 0) = 0;   figure(10)   subplot(1,2,1)   Ima = uint8(Ima);   histo = histogram(Ima);   bar(0:255, histo, 'histc')   xlim([0 255])   set(gca,'XTick',[0:15:255]);   title('Original Histogram')   grid 'on'   subplot(1,2,2)   newIma = uint8(newIma);   histo = histogram(newIma);   bar(0:255, histo, 'histc')   xlim([0 255])   set(gca,'XTick',[0:15:255]);   grid 'on'   title('New Histogram')       while(true)      c = lower(input('Would you like to try a different operation? y/n: ','s'));      if(c == 'y')        break      else        msgbox('You are now working with a image with its histogram transformed!!');        break      endif    end  endend