function thresIma = imBWhsv(Ima, im, names) Ima = double(rgb2hsv(Ima)); ih = Ima(:,:,1); is = Ima(:,:,2); iv = Ima(:,:,3);  figure(6) subplot(2,2,1) imagesc(ih); title('Test Image HUE plane'); subplot(2,2,2) imagesc(is);	 title('Test Image SATURATION plane'); subplot(2,2,3) imagesc(iv); title('Test Image VALUE plane');  subplot(2,2,4) image(hsv2rgb(Ima)); title('Test Image HSV');  msgbox('Working with the HUE plane') ih = imBW(ih, im, names); msgbox('Working with the SATURATION plane') is = imBW(is, im, names); msgbox('Working with the VALUE plane') iv = imBW(iv, im, names);  thresIma = ih|is|iv;  figure(6) subplot(2,2,1) imshow(ih, [0 1]); title('Test Image HUE plane'); subplot(2,2,2) imshow(is, [0 1]);	 title('Test Image SATURATION plane'); subplot(2,2,3) imshow(iv, [0 1]); title('Test Image VALUE plane');  subplot(2,2,4) imshow(thresIma, [0 1]); title('Test Image composite');end