function thresIma = imBWrgb(Ima, im, names) ir = Ima(:,:,1); ig = Ima(:,:,2); ib = Ima(:,:,3);  figure(6) subplot(2,2,1) imshow(ir, [0 255]); title('Test Image RED plane'); subplot(2,2,2) imshow(ig, [0 255]);	 title('Test Image GREEN plane'); subplot(2,2,3) imshow(ib, [0 255]); title('Test Image BLUE plane');  subplot(2,2,4) imshow(Ima, [0 255]); title('Test Image RGB');   msgbox('Working with the RED plane') ir = imBW(ir, im, names); msgbox('Working with the GREEN plane') ig = imBW(ig, im, names); msgbox('Working with the BLUE plane') ib = imBW(ib, im, names);  thresIma = ir|ig|ib;  figure(6) subplot(2,2,1) imshow(ir, [0 1]); title('Test Image RED plane'); subplot(2,2,2) imshow(ig, [0 1]);	 title('Test Image GREEN plane'); subplot(2,2,3) imshow(ib, [0 1]); title('Test Image BLUE plane');  subplot(2,2,4) imshow(thresIma, [0 1]); title('Test Image composite');end