function [coins, names] = loadSet(imSet)%function to load the set of working images%imSet = 1; load Part 1 gray scale set of images%imSet = 2; load%Part 1 rgb set of images%imSet = 3; load Part 2 set 1 images%imSet = 4; load Part 2 set 2 imagesswitch imSet        case 1        clear coins names files        coins = uint8(zeros(512,512,1,6));        files = {'DSCN0563Gray4_C1','DSCN0566Gray4_C1','DSCN0569Gray4_C1',...            'DSCN0572Gray4_C1','DSCN0576Gray4_C1','DSCN0579Gray4_C1'};        for i = 1:6            coins(:,:,1,i) = imread(char(strcat('Gray_Part1/',files(i),'.png')));        end        names = {'DSCN0563Gray4\_C1','DSCN0566Gray4\_C1','DSCN0569Gray4\_C1',...            'DSCN0572Gray4\_C1','DSCN0576Gray4\_C1','DSCN0579Gray4\_C1'};        clc        return            case 2        clear coins names files        coins = uint8(zeros(512,512,3,6));        files = {'DSCN0563Color4_C1','DSCN0566Color4_C1','DSCN0569Color4_C1',...            'DSCN0572Color4_C1','DSCN0576Color4_C1','DSCN0579Color4_C1'};        for i = 1:6            coins(:,:,:,i) = imread(char(strcat('Color_Part1/',files(i),'.png')));        end        names = {'DSCN0563Color4\_C1','DSCN0566Color4\_C1','DSCN0569Color4\_C1',...            'DSCN0572Color4\_C1','DSCN0576Color4\_C1','DSCN0579Color4\_C1'};        clc        return            case 3        clear coins names files        coins = uint8(zeros(900,1000,3,6));        files = {'DSCN0616Color2_C1','DSCN0619Color2_C1','DSCN0622Color2_C1',...            'DSCN0625Color2_C1','DSCN0629Color2_C1','DSCN0632Color2_C1'};        for i = 1:6            coins(:,:,:,i) = imread(char(strcat('Set1_Part2/',files(i),'.png')));        end        names = {'DSCN0616Color2\_C1','DSCN0619Color2\_C1','DSCN0622Color2\_C1',...            'DSCN0625Color2\_C1','DSCN0629Color2\_C1','DSCN0632Color2\_C1'};        clc        return            case 4        clear coins        coins = uint8(zeros(600,600,3,10));        files = dir('Set2_Part2');        for i = 1:10            coins(:,:,:,i) = imread(char(strcat('Set2_Part2/',files(2+i).name)));        end        names = {'DSCN0582Color4\_C1','DSCN0585Color4\_C1','DSCN0588Color4\_C1',...            'DSCN0595Color4\_C1','DSCN0598Color4\_C1',...            'DSCN0601Color4\_C1', 'DSCN0604Color4\_C1','DSCN0607Color4\_C1',...            'DSCN0610Color4\_C1', 'DSCN0613Color4\_C1'};        clc        returnendend