function histo = viewHistogram(Ima, im, names)%Function to visualize the image alongside its histogram% Ima, image values% im, test image number% names, test images names% opt, option for the plots, [1]: single [2]: subplotopt = input('Option for the plot? [1]: single [2]: subplot: ');histo = histogram(Ima);figure(2)s1 = subplot(1,2,1);s2 = subplot(1,2,2);if opt == 1	if isa(Ima, 'logical')		xl = 1;		st = 0.5;	else		xl = 255;		st = 15;	end	figure(2)	delete(s1);	delete(s2);	bar(0:xl, histo, 'histc')	%stem(histo, 'Marker','none')	xlim([0 xl])	%if max(histo) > 6000	% yim([0 6000])	%endif	set(gca,'XTick',[0:st:xl]);	grid 'on'	title(strcat('Test Image',' #', num2str(im), ': ', names(im), ' - Histogram'));	colormap gray;endif opt == 2	if isa(Ima, 'logical')		xl = 1;		st = 1;	else		xl = 255;		st = 15;	end		s1 = subplot(1,2,1);	if(isa(Ima, 'logical'))		imagesc(Ima); colormap gray;		title('Thresholded Image');		xl = 1;		st = 1;	else		imshow(Ima, [0 255])		title(strcat('Test Image',' #', num2str(im), ': ', names(im)));		xl = 255;		st = 15;	end		s2 = subplot(1,2,2);	bar(0:xl, histo, 'histc')	xlim([0 xl])	%if max(histo) > 6000	%ylim([0 6000])	%endif	set(gca,'XTick',0:st:xl);	grid 'on'	if xl == 1		title('Thresholded Test Image Histogram')	else		title('Test Image Histogram')	end	endend