function viewImage(Ima, im, names, fig)% Function to visualize the test image% viewImage(Ima, im, names):%	Ima: image values%	im: test image number%	names: test images names figure(fig) if(isa(Ima, 'logical'))  imagesc(Ima); colormap gray  title('Thresholded Image'); else  imagesc(Ima)  if size(size(Ima)) == 2	  colormap gray  end  title(strcat('Test Image',' #', num2str(im), ': ', names(im))); endend